@import "./base.scss";

.services {
    background: black;
    padding: 50px;
    color: #dfd2b8; 
    position: relative;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 50px;
    flex-wrap: wrap;
    
    &__box {
        position: relative;
        width: 20rem;
        height: 280px;
        background: #080715;
        border-radius: 20px;
        overflow: hidden;
        .icon {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            transition: 0.5s;
            display: flex;
            justify-content: center;
            align-items: center;
            z-index: 1;
            img {
                filter: invert(100%);
                width: 35%;
            }

           
            transition-delay: 0.25s;
           

        } 
        .red {
            background: linear-gradient(to right, #46F192, #37b871 );
        }

        .blue {
            background: linear-gradient(to right, #271ecc, #221aae );
        }

        .purple {
            background: linear-gradient(to right, #511430, #5d1738);
        }
        
        &:hover .icon {
            top: 20px;
            left: calc(50% - 40px);
            width: 80px;
            height: 80px;
            border-radius: 50%;
        }

        
        .content {
            position: relative;
            padding: 10px;
            color: $cream-white;
            text-align: center;
            margin-top: 50px;
            z-index: 1;
            transform: scale(0);
            transition: 0.5s;
            transition-delay: 0s;
        }

        &:hover .content {
            transform: scale(1);
            transition-delay: 0.25s;
            
        }

        .content h2 {
            font-size: 24px;
            transition-delay: 0.25s;
        }

        .content p {
            line-height: 1.5rem;
            font-size: 1rem;
            padding-bottom: 15rem;
            transition-delay: 0.25s;
        }
    }
}
