@import "./base.scss";

.contact {
    position: relative;
    width: 100%;
    background: black;
    &__header {
        margin-bottom: 3rem;
    }
    @media (max-width: 1100px) {
        padding: 2rem;
    } 

}

.form {
    &__info {
        
        display: flex;
        align-items: center;
        gap: 1rem;
        font-size: 2.2vw;
        font-weight: 500;
        input {
            font-size: inherit;
            font-weight: inherit;
            border: none;
            background: $cream-white;
            border-bottom: 1px solid #000;
            color: #444;
            &:focus {
                outline: none;
            }
        }

    }
    &__flex {
        display: flex;
        gap: 1rem;
        margin-bottom: 2rem;
        textarea {
            width: 70%;
            background: transparent;
            border: none;
            border-bottom: 1px solid #000;
            font-size: 2.2vw;
            color: #444;
            resize: none;
            &:focus {
                outline: none;
            }
        }
        .circleButton {
            font-weight: 900;
            font-size: 20px;
            &:before {
                background: #000;
            }
            &:after {
                display: none;
            }
            &:hover:before {
                background: #fff;
            }
        }
    }
}

@media (max-width: 900px) {
    .form {
        &__flex {
            flex-direction: column;
            textarea {
                font-size: 4vw;
            }
        }
        &__info {
            font-size: 4vw;
            input {
                font-size: 4vw;
            }
        }
    }
} 

@media (max-width: 800px) {
    .contact .homeButton {
        transform: scale3d(1,1,1);
        font-size: 19px;
    }

    
} 

@media (max-width: 610px) {
    .form {
        &__info {
            flex-direction: column;
            align-items: flex-start;
            width: 100%;
            input {
                font-size: 5vw;
                width: 100%;
            }
        }
        &__flex {
            textarea {
                width: 100%;
                font-size: 5vw;
            }
        }
    }
}
