@import "./base.scss";
@import url('https://fonts.googleapis.com/css2?family=Roboto:wght@300&family=Syne&display=swap');
@import url('http://fonts.cdnfonts.com/css/helvetica-neue-9');
@import url('http://fonts.cdnfonts.com/css/futura-md-bt');
@import url('http://fonts.cdnfonts.com/css/avenir-next-lt-pro');


@font-face {
    font-family:"Devant";
    src: url(../fonts/Devant-Horgen.ttf);
}

.landing {
    font-family: 'Avenir Next LT Pro', sans-serif;                                
    position: relative;
    width: 100%;
    height: 100vh;
    color: #dfd2b8;
    overflow: hidden;
    .header {
        position: absolute;
        z-index: 1;
        top: 2rem;
        left: 50%;
        transform: translateX(-40%);
        
        &__left {
            transform: translateX(-80%);

        }
    

        &__wrap {
            padding: 20px;
            display: flex;
            align-items: center;
            justify-content: space-between;
            width: 50vw;
        }

        

        &__left {
            display: flex;
            align-items: center;
            gap: 2.3rem;
            & li {
                font-size: 18px;
                font-weight: 800px;
            }
        }
        @media(max-width: 1200px) {
            &__wrap {
                width: 100vw;
            }
        }

        @media(max-width: 650px) {
            &__sm {
                display: none;
            }
        }

        @media(max-width: 370px) {
            &__left {
                left: 36%;
            }
            &__wrap {
                gap: 20%;
            }
            .link[data-text="Home"] {
                display: none;
            }
        }
    }


    .socials {
        position: absolute;
        left: 50%;
        transform: translateX(-50%);
        bottom: 10rem;
        display: flex;
        align-items: center;
        gap: 1rem;
        z-index: 2;
    }

    .scrollDown {
        position: absolute;
        bottom: 3rem;
        left: 50%;
        transform: translateX(-50%);
        &__wheel {
            height: 42px;
            width: 24px;
            border-radius: 10px;
            border: 2px solid white;
            &--inner {
                position: absolute;
                left: 10px;
                margin-top: 5px;
                width: 4px;
                height: 4px;
                background: white;
                border-radius: 50%;
                animation: movingwheel .5s linear infinite;
                @keyframes movingwheel {
                    0% {
                        top: 1px;
                    }
                    25% {
                        top: 2px;
                    }
                    50% {
                        top: 3px;
                    }
                    75% {
                        top: 2px;
                    }
                    100% {
                        top: 1px;
                    }
                }
            }

        }

        .scrollDown__arrows {
            display: flex;
            flex-direction: column;
            align-items: center;
            .scrollDown__arrow {
                width: 15px;
                height: 15px;
                border-right: 2px solid white;
                border-bottom: 2px solid white;
                transform: rotate(45deg);
                animation: scrollAnimation 1s infinite;
                animation-direction: alternate;
                &:nth-child(1) {
                    animation-delay: 0.1s;
                }
                &:nth-child(2) {
                    animation-delay: 0.2ss;
                }
                &:nth-child(3) {
                    animation-delay: 0.3s;
                }
                @keyframes scrollAnimation {
                    0% {
                        opacity: 0;
                    }
                    50% {
                        opacity: 0.5;
                    }
                    100% {
                        opacity: 1;
                    }
                }



            }
        }


    }
    

    .introTitle {
        font-family: 'Avenir Next LT Pro', sans-serif;        
        position: absolute;
        top: 20%;
        left: 3rem;
        width: 100%;
        height: 60%;
        z-index: 2;
        color: #dfd2b8;
        
        
        & h4 {
            font-size: 3px;
            width: 75%;
            
        }
        & h1 {
            font-family: 'Devant'; 
            font-size: 100px;
            width: 100%;
            padding-top: 1rem;
            
        }
        @media (min-width: 600px) {
            & h1 {
                font-size: 160px;
            }
            & h4 {
                font-size: 20px;
            }
        }
        @media (min-width: 1200px) {
            & {
                left: 15%;
            }
            & h1 {
                font-size: 100px;
            }
            & h4 {
                font-size: 40px;
            }
        }
    
    }

     
} // end

.landing canvas {
    position: absolute;

}
