  flex: 1;
  background-color: var(--body-bg);
  padding: 5rem 3rem;
  border-radius: 12px;
  box-shadow: 0px 0px 20px rgba(0, 0, 0, 0.1568627451);
  z-index: 2;
}

#projects .item:nth-of-type(even) .right {
  margin-left: 0;
}

