  height: 100%;
  overflow: hidden;
  border-radius: 12px;
  overflow: hidden;
  box-shadow: 0px 0px 20px rgba(71, 5, 252, 0.2);
  position: relative;
}

#projects .item .left .img::after {
  content: "";
  position: absolute;
  left: 0;
  top: 0;
  height: 100%;
  width: 100%;
  background-color: rgba(90, 36, 237, 0.2196078431);
}

